/*!
 * Module dependencies.
 */

var util = require('util'),
	utils = require('keystone-utils'),
	super_ = require('../field');

/**
 * TextArray FieldType Constructor
 * @extends Field
 * @api public
 */

function grid(list, path, options) {
	this._nativeType = [String];

	this._underscoreMethods = ['crop'];
	grid.super_.call(this, list, path, options);
}

/*!
 * Inherit from Field
 */

util.inherits(grid, super_);


/**
 * Crops the string to the specifed length.
 *
 * @api public
 */

grid.prototype.crop = function(item, length, append, preserveWords) {
	utils.cropString(item.get(this.path), length, append, preserveWords);
};

/**
 * Updates the value for this field in the item from a data object
 *
 * @api public
 */

grid.prototype.updateItem = function(item, data) {
	if ( data[this.path] === undefined ) {
		item.set(this.path, []);
	} else {
		var newdata = [];
		var length = this.options.length;
		for( var i = 0; i < data[this.path].length/length; i++ ) 
			newdata[i] = data[this.path].slice(i*length,(i+1)*length);
		item.set(this.path, newdata);
	}
};

/*!
 * Export class
 */

exports = module.exports = grid;
